import Postcard from './Postcard.js'

function convertPostDates(posts) {
    posts = posts.map(post => {
        post.frontmatter.date = new Date(post.frontmatter.date);
        return post;
    });
    return posts;
}

export default function PostList({ posts }) {
    if (posts === 'undefined') return null;

    posts = convertPostDates(posts).slice().sort((a, b) => b.date - a.date).reverse();
    
    posts = JSON.stringify(posts.map(post => post.frontmatter.date.toISOString().slice(0,10)));

    return (
        <div>
            {!posts && <div>No posts!</div>}
            <ul>
            {posts &&
                posts.map((post, index) => {
                    if (post.frontmatter.status === "published") {
                    return (
                        <Postcard post={post} key={index}/>
                    )}
                })}
            </ul>
        </div>
    )
}
